INSERT INTO `tbl_services_loft_conversions_type` (`id`, `title`, `description`, `image`, `is_active`, `created_at`) VALUES
(2, 'Dormer Loft Conversion', 'These are the most popular Loft Conversion Option because they are the easiest way to increase roof space and add light to your loft. The structure of your roof is altered on the sides or rear of the property and a new flat-roofed dormer is added. ', 'service_85768.jpg', 1, '2019-04-23'),
(4, 'Hip to Gable Loft Conversion', 'These are most commonly found on the side of detached, semi-detached or end-terraced properties. In this process, the hip also known as the sloping side of the roof is removed and the wall is then built straight to form a new vertical gable.', 'service_60755.jpg', 1, '2019-12-20'),
(6, 'Mansard Loft Conversion', 'This type of Loft Conversion is great at creating extra internal space. This process involves the replacement of one or both roof slopes that have very steep sloping sides and a flat roof on top. Ideal for semi-detached and detached properties.', 'service_40533.jpg', 1, '2019-12-20'),
(7, 'Velux Loft Conversion', 'This is the most cost-effective and simplest Loft Conversion as you will be able to increase your loft space without altering the shape of the roof structure. Very little disruption and construction is required as all is done internally. Ideal for all types of properties.', 'service_61638.jpg', 1, '2019-12-20');
INSERT INTO `tbl_testimonials` (`id`, `name`, `company`, `position`, `image`, `description`, `display_order`) VALUES
